mapper.select:
select
a.goods_id as goods_id, a.category_id as category_id,a.nickname as nickname,a.goods_name as goods_name, a.image as image, a.simple_describe as simple_describe,a.is_marketable as is_marketable, b.category_name as detail_describe, a.recommend as recommend, a.del_state as del_state, a.create_time as create_time, a.update_time as update_time, a.admin_id as admin_id
from t_goods as a left join t_goods_category as b on a.category_id = b.category_id where a.del_state=1
and a.is_marketable = #{isMarketable,jdbcType=TINYINT}
and a.create_time <= #{createTime_le,jdbcType=TIMESTAMP}
and a.create_time >= #{createTime_ge,jdbcType=TIMESTAMP}
and a.goods_name LIKE #{goodsName,jdbcType=VARCHAR}
and (a.category_id=#{categoryId,jdbcType=TINYINT} or a.category_id in ( select category_id from t_goods_category where category_id=#{categoryId,jdbcType=TINYINT} or parent_id=#{categoryId,jdbcType=TINYINT}))
order by a.create_time asc

select
a.goods_id as goods_id, a.category_id as category_id,a.nickname as nickname,a.goods_name as goods_name, a.image as image, a.simple_describe as simple_describe,a.is_marketable as is_marketable, b.category_name as detail_describe, a.recommend as recommend, a.del_state as del_state, a.create_time as create_time, a.update_time as update_time, a.admin_id as admin_id
from t_goods as a left join t_goods_category as b on a.category_id = b.category_id where a.del_state=1
and a.is_marketable = #{isMarketable,jdbcType=TINYINT}
and a.create_time <= #{createTime_le,jdbcType=TIMESTAMP}
and a.create_time >= #{createTime_ge,jdbcType=TIMESTAMP}
and a.goods_name LIKE #{goodsName,jdbcType=VARCHAR}
and (a.category_id=#{categoryId,jdbcType=TINYINT} or a.category_id in ( select category_id from t_goods_category where category_id=#{categoryId,jdbcType=TINYINT} or parent_id=#{categoryId,jdbcType=TINYINT}))
order by a.create_time asc

select

from t_goods
where 1=1 

		and	goods_name = #{goodsName,jdbcType=VARCHAR}


		and	nickname = #{nickName,jdbcType=VARCHAR}



		and	simple_describe = #{simpleDescribe,jdbcType=VARCHAR}


		and	is_marketable =#{isMarketable,jdbcType=TINYINT}


		and	detail_describe = #{detailDescribe,jdbcType=VARCHAR}


		and	recommend = #{recommend,jdbcType=TINYINT}


		and	del_state = #{delState,jdbcType=TINYINT}



		and	admin_id = #{adminId,jdbcType=INTEGER}

order by recommend,update_time desc
limit 1
